mapper.sql:
where 1=1

      and seqNo like concat('%', #{seqNo}, '%')


      and eventType = #{eventType}


    and id = #{id}

mapper.select:
select * from t_event_log


     order by ${sorts}


     limit ${pages.page_startIndex},${pages.page_pageSize}

mapper.update:
update t_event_log
responseBody=#{responseBody}, seqNo=#{seqNo}, status=#{status}, responseTime=#{responseTime},
where id = #{id}
